queries.query:
# Returns the Price Paid data from the default graph for each transaction record having# an address with the given postcode.# The postcode to query is set in the line - ?address_instance common:postcode "PL6 8RU"^^xsd:string .SELECT ?paon ?saon ?street ?town ?county ?postcode ?amount ?dateWHERE{  ?transx lrppi:pricePaid ?amount ;          lrppi:transactionDate ?date ;          lrppi:propertyAddress ?addr.    ?addr lrcommon:postcode "PL6 8RU"^^xsd:string.  ?addr lrcommon:postcode ?postcode.    OPTIONAL {?addr lrcommon:county ?county}
  OPTIONAL {?addr lrcommon:paon ?paon}
  OPTIONAL {?addr lrcommon:saon ?saon}
  OPTIONAL {?addr lrcommon:street ?street}
  OPTIONAL {?addr lrcommon:town ?town}

}
ORDER BY ?amount
