queries.query:
# Returns the House Price Index from the default graph for particular Local Authority / Region

SELECT DISTINCT ?region ?yearmonth ?indexr ?avgprice ?monthly ?annual ?volume
WHERE
{ 
  <http://landregistry.data.gov.uk/data/hpi/region/City-of-Plymouth/month/2013-02>
    lrhpi:refRegionName ?region ;
    lrhpi:indicesSASM ?indexSASM ;
    lrhpi:refPeriod ?yearmonth ;
    lrhpi:averagePricesSASM ?avgprice ;
    lrhpi:monthlyChange ?monthly ;
    lrhpi:annualChange ?annual.

  OPTIONAL {
    <http://landregistry.data.gov.uk/data/hpi/region/City-of-Plymouth/month/2013-02> 
      lrhpi:salesVolume ?volume . 
  }

  BIND (ROUND(xsd:decimal(?indexSASM) * 100)/100 AS ?indexr)
}

# Returns the Price Paid data from the default graph for each transaction record having
# an address with the given postcode.
# The postcode to query is set in the line - ?address_instance common:postcode "PL6 8RU"^^xsd:string .


SELECT ?paon ?saon ?street ?town ?county ?postcode ?amount ?date
WHERE
{
  ?transx lrppi:pricePaid ?amount ;
          lrppi:transactionDate ?date ;
          lrppi:propertyAddress ?addr.

  ?addr lrcommon:postcode "PL6 8RU"^^xsd:string.
  ?addr lrcommon:postcode ?postcode.

  OPTIONAL {?addr lrcommon:county ?county}
  OPTIONAL {?addr lrcommon:paon ?paon}
  OPTIONAL {?addr lrcommon:saon ?saon}
  OPTIONAL {?addr lrcommon:street ?street}
  OPTIONAL {?addr lrcommon:town ?town}

}
ORDER BY ?amount
